import React, { useState , useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function ShowModal({Show,handleclose}) {

 
 
 

  return (
  
    <>
      

      <Modal show={Show} onHide={handleclose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
          <input type="text"  value={name} placeholder='Enter your name'></input><br></br>

          <input type="Number"  value={age} placeholder='Enter your age'></input><br></br>

          <input type="text"  value={role} placeholder='Enter your role'></input>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleclose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleclose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ShowModal;
