import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import "./newdesign.css";
import { emailFunc } from "./Backenddata";

function CheckEmail() {
  return (
    <>
      <Formik
        initialValues={{ email: "" }}
        validate={(values) => {
          const errors = {};
          console.log();

          if (!values.email) {
            errors.email = "fill the blanks";
          } else if (
            values.email == "/^w+([.-]?w+)*@w+([.-]?w+)*(.w{2,3})+$/"
          ) {
            errors.email = "enter valid format";
          }

          return errors;
        }}
        onSubmit={async (values, { setErrors, setSubmitting }) => {
          try {
            if (values.email) {
              const response = await emailFunc(values.email);
              if (response.status === 200) {
                console.log(response.data);
              }
            }
          } catch (error) {
            if (error.status === 422) {
              setErrors({ common: error.message });
            }
            console.log(error.message);

            setSubmitting(false);
          }
        }}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          verifyOtp,
          /* and other goodies */
        }) => (
          <div>
            <center>
              <h2>Forgot Password</h2>
              <form>
                <div class="checkEmail">
                  <input
                    type="email"
                    name="email"
                    placeholder="Enter email.."
                  ></input>
                  <br></br>
                  <br></br>
                  <Button type="submit" id="emailbtn" onClick={handleSubmit}>
                    Verify Email
                  </Button>
                </div>
              </form>
            </center>
          </div>
        )}
      </Formik>
    </>
  );
}
export default CheckEmail;
