import  Modal  from "react-bootstrap/Modal";
import { useFormik,FormikProvider } from "formik";
import {Field,Form,Formik,Formikprops} from 'formik';
import  Button  from "react-bootstrap/Button";
import {postFunc,updateFunc,deleteFunc} from "./Backenddata";

function Edit(){
initialValues = { name: '', email: '', password: '', age: '' ,role:''}
   
validate = (values) => {
    const errors = {};
    if (!values.name) {
       errors.name = 'Required';
    }
    if (!values.amount) {
       errors.amount = 'Required';
    }
    if (!values.date) {
       errors.date = 'Required';
    }
    if (!values.category) {
       errors.category = 'Required';
    }
    return errors;
 }
 handleSubmit = (values, setSubmitting) => {
    setTimeout(() => {
       alert(JSON.stringify(values, null, 2));
       setSubmitting(false);
    }, 400);
 }

    return (
        <>
       <div id="expenseForm">
          <Formik
             initialValues={this.initialValues}
             validate={values => this.validate(values)}
             onSubmit={(values, { setSubmitting }) => this.handleSubmit(values, setSubmitting)} > 
             {
                ({
                   values,
                   errors,
                   touched,
                   handleChange,
                   handleBlur,
                   handleSubmit,
                   isSubmitting,
                   /* and other goodies */
                }) => 
                (
                   <form onSubmit={handleSubmit}>
                      <label for="name">Name<span>{errors.name && touched.name && errors.name}</span></label>
                      <input type="text" id="name" name="name" placeholder="Enter name"
                         onChange={handleChange}
                         onBlur={handleBlur}
                         value={values.name} />

                      <label for="email">Email <span>{errors.email && touched.email && errors.email}</span></label>
                      <input type="email" id="email" name="email" placeholder="Enter email"
                         onChange={handleChange}
                         onBlur={handleBlur}
                         value={values.email} />

                      <label for="password">Password <span>{errors.password && touched.password && errors.password}</span></label>
                      <input type="password" id="password" name="password" placeholder="Enter password"
                         onChange={handleChange}
                         onBlur={handleBlur}
                         value={values.password} />

                      <label for="age">Age <span>{errors.age && touched.age&& errors.age}</span></label>
                      <input type="number" id="age" name="age" placeholder="Enter age"
                         onChange={handleChange}
                         onBlur={handleBlur}
                         value={values.password} />

                      <label for="role">Role <span>{errors.role && touched.role && errors.role}</span></label>
                      <select id="role" name="role"
                         onChange={handleChange}
                         onBlur={handleBlur}
                         value={values.role}>
                         <option value="">Select</option>
                         <option >User</option>
                         <option >System</option>
                         <option>Admin</option>
                      </select>

                      <input type="submit" value="Submit" disabled={isSubmitting} />
                   </form>
                )
             }
          </Formik>
       </div>
       </>
    )
            }
 

export default Edit;
