import { useState, useEffect } from "react";
import React from 'react';
import { Formik,useFormik, Field } from 'formik';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import ShowModal from "./ShowModal";
import Form from 'react-bootstrap/Form';
import axios from'axios';


const NewFormik = () => {
 const [data, setData] = useState(null);
 const [show, setShow] = useState(false);
 const handleClose = () => setShow(false);
 const handleShow = () => setShow(true);
 const[name,setName] =useState("");
 const[age,setAge] =useState("");
 const[role,setRole] =useState("");
 const[isError,SetIsError] = useState(false);
 
  useEffect(() => {
       fetchdata();
    }, []);
    
 const fetchdata = async() =>{
   try{
     const response=await fetch("http://localhost:3000/newusers")
     const json = await response.json()
     setData(json)
   }catch(error){
     console.log("error",error)
   }
 }
 console.log(data)


const postinfo= async()=>{
  try{
    axios
    .post("http://localhost:3000/addNewUser",
    {
      name,age,role
    })
    .then((response) =>{
      fetchdata();
    })

  }catch(error){
   console.log("error",error)
  }
}




return(
    <>
    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
 
    <h1>Form using formik</h1>
    <Formik
      initialValues={{ name: '', age: '' ,role: '' ,email:'',password:''}}
      validate={values => {
        const errors = {};
        if(values.name==''||values.age==''||values.role==''||values.email==''||values.password==''){
            errors.all='fill all details';
        }
        else if(values.email=='/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/'){
           errors.all='enter valid format';
        }
        else if (!values.name || !values.age || !values.role || !values.email|| !values.password) {
          errors.all = 'Required';
          postinfo();
        }
        return errors;
      
      }}
      onSubmit={(values, { setSubmitting }) => {
        const fetchdata = async() =>{
            try{
              const response=await fetch("http://localhost:3000/users")
              const json = await response.json()
              setData(json)
            }catch(error){
              console.log("error",error)
            }
          }
          console.log(data)
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        /* and other goodies */
      }) => (
        <form onSubmit={handleSubmit}>
          <input
           
            type="text"
            name="name"
            placeholder='Enter your name..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.name}
          /><br></br><br></br>
          {errors.name && touched.name&& errors.name}
          <input
            type="number"
            name="age"
            placeholder='Enter your age..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.age}
          /><br></br><br></br>
           {errors.age && touched.age && errors.age}
          <input
            name="role"
            placeholder='Enter your role..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.role}
          /><br></br><br></br>
          <input
            type="email"
            name="email"
            placeholder='Enter your email..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.email}
          /><br></br><br></br>
          <input
            type="password"
            name="password"
            placeholder='Enter your password..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.password}
          /><br></br><br></br>
          {errors.role && touched.role && errors.role}
          <button type="submit" disabled={isSubmitting}>
            Submit
          </button>
        </form>
      )}
    </Formik>
    </Modal.Body>
        <Modal.Footer>
          {/* <Button type="submit" onClick={(e)=>formSubmit(e)}>submit</Button> */}
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
    
        </Modal.Footer>
      </Modal>

  </>
)};

export default NewFormik;
