import { useEffect, useState } from "react";
function Form(){
    const[email,setEmail] =useState("");
    const[password,setPassword] =useState("");
    const[errormessage,setErrorMessage] = useState("");
    const[isError,SetIsError] = useState(false);


    const emailOnChange=(e)=>{
        setEmail(e.target.value);
         }
         const passwordOnChange=(e)=>{
            setPassword(e.target.value);
             }

    const formSubmit=(e) => {
        e.preventDefault();
        if(email =="" || password==""){
            SetIsError(true)
            setErrorMessage("details should be filled");
        }
    }


    return(
        <form onSubmit={formSubmit}>
         <input type="email"  name="email" onChange={emailOnChange} placeholder="Enter your email"></input><br></br>
         {isError && errormessage && <span>{errormessage}</span>}
         <input type="password"  name="password" onChange={passwordOnChange} placeholder="Enter your password"></input><br></br>
         {isError && errormessage && <span>{errormessage}</span>}
         <input type="submit" />
         </form>
    )
}
export default Form;
