import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import {
  users,
  clearState,
  extraReducers,
  getUsers,
  userSelector,
} from "./Redux/Reducers/UserReducer";
import { getData } from "./services";

export default function User() {
  const dispatch = useDispatch();
  const { isFetching, errorMessage, isError, users } =
    useSelector(userSelector);

  useEffect(() => {
    dispatch(clearState());
    dispatch(getUsers());
  }, []);

  return (
    <>
      {isFetching ? (
        <div>loading</div>
      ) : (
        <div>
          <>
            <Table class="table-info" striped bordered hover variant="dark">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Password</th>
                  <th>Age</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {data && data.length && (
                  <>
                    {data.map((element, index) => {
                      return (
                        <tr>
                          <td>{index}</td>
                          <td>{element.name}</td>
                          <td>{element.email}</td>
                          <td>{element.password}</td>
                          <td>{element.age}</td>
                          <td>{element.role}</td>
                          <td>
                            <Button
                              onClick={() => handleAction(element._id, "edit")}
                            >
                              Edit
                            </Button>
                            &nbsp;&nbsp;&nbsp;
                            <Button
                              onClick={() =>
                                handleAction(element._id, "delete")
                              }
                            >
                              Delete
                            </Button>
                          </td>
                        </tr>
                      );
                    })}
                  </>
                )}
              </tbody>
            </Table>
            <Button onClick={handleShow}>Add User</Button>
            <Model
              show={show}
              handleClose={handleClose}
              user={user}
              setShow={setShow}
              action={action}
            />
            <DeleteModel
              show={deleteshow}
              handleDelete={handleDelete}
              handleDeleteShow={handleDeleteShow}
            />
          </>
        </div>
      )}
    </>
  );
}
