import { useState, useEffect } from "react";
import React from 'react';
import { Formik , Field } from 'formik';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import axios from'axios';

// const input = ({field,form, ...props}) =>{
//     return <input>{...field}{...props}</input>
// }
const NewFormik = () => {
 const [data, setData] = useState(null);
 const [show, setShow] = useState(false);
 const [newuser,setNewuser] = useState({ name: '', age: '' ,role: '' ,email:'',password:''});
 const [deleteuser,setDeleteuser] = useState(false);
 const [action,setAction] = useState("add");
 const handleClose = () => setShow(false);
 const handleShow = () => setShow(true);

 

 
 
    
 const fetchdata = async() =>{
   try{
     const response=await fetch("http://localhost:3000/newusers")
     const json = await response.json()
     setData(json)
   }catch(error){
     console.log("error",error)
   }
 }
 console.log(data)

 useEffect(() => {
    fetchdata();
 }, []);

 const handleAction = (id,action) =>{
     const user = data.find((user)=> user._id === id)
     if(user){
         setNewuser(user);
         setAction(action);
     }
     if(action === "edit"){
         handleShow();
     }
 }

 
  




return(
    <>
    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
 
    <h1>Form using formik</h1>
    <Formik
      initialValues={{ name: '', age: '' ,role: '' ,email:'',password:''}}
      validate={values => {
        const errors = {};
        if(!values.name){
            errors.all='fill the blanks';
        }else if(values.name<4){
            errors.all='name must be of 3 characters';
        }
        if(!values.email){
            errors.all='fill the blanks';
        }
        else if(values.email=='/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/'){
               errors.all='enter valid format';
            }
            
        if(!values.age){
            errors.all='fill the blanks';
        }
    
        if(!values.role){
            errors.all='fill the blanks';
        }
        return errors;
      
      }}


      
      onSubmit={({name,email,password,age,role}, { setSubmitting }) => {
        const fetchdata = async() =>{
            try{
              const response=await fetch("http://localhost:3000/newusers")
              const json = await response.json()
              setData(json)
            }catch(error){
              console.log("error",error)
            }
          }
          console.log(data)

          const postinfo= async()=>{
            try{
              axios
              .post("http://localhost:3000/addNewUser",
              {
               name,email,password,age,role
              })
              .then((response) =>{
                fetchdata();
              })
          
            }catch(error){
             console.log("error",error)
            }
          }
          postinfo();
          fetchdata();
     
          
         
        setTimeout(() => {
          alert(JSON.stringify(name,email,password,age,role, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        /* and other goodies */
      }) => (
        <form onSubmit={handleSubmit}>
          <input
           
            type="text"
            name="name"
            placeholder='Enter your name..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.name}
          /><br></br>
          {errors.name && touched.name&& errors.name}

          <input
            type="email"
            name="email"
            placeholder='Enter your email..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.email}
          /><br></br>
         {errors.email && touched.email && errors.email}
         <input
            type="password"
            name="password"
            placeholder='Enter your password..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.password}
          /><br></br>
          {errors.password && touched.password && errors.password}
          <input
            type="number"
            name="age"
            placeholder='Enter your age..'
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.age}
          /><br></br>
           {errors.age && touched.age && errors.age}

          
          <Field as="select" name="role">
            <option value="user">User</option>
            <option value="system">System</option>
             <option value="admin">Admin</option>
             </Field><br></br>
             
            
             <br></br>
        <br></br>
         
          <button type="submit" disabled={isSubmitting}>
            Submit
          </button>
        </form>
        
      )}
    </Formik>
    </Modal.Body>
        <Modal.Footer>
          {/* <Button type="submit" onClick={(e)=>formSubmit(e)}>submit</Button> */}
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
    
        </Modal.Footer>
      </Modal>


      
   <Table striped bordered hover>
   <thead>
     <tr>
       <th>ID</th>
       <th>Name</th>
       <th>Age</th>
       <th>Role</th>
       <th>Email</th>
       <th>Password</th>
       <th>Actions</th>
     
      
     </tr>
   </thead>
   <tbody>
     {data && data.length && (<>
     {data.map((element,index) => {
       return(
         <tr>
           <td>{index}</td>
           <td>{element.name}</td>
           <td>{element.age}</td>
           <td>{element.role}</td>
           <td>{element.email}</td>
           <td>{element.password}</td>
           <td><Button type="success" name="edit" id="edit" onClick={()=>handleAction(element._id,"edit")}>Edit</Button>
          <Button variant="success" name="delete" id="delete" onClick={()=>handleAction(element._id,"delete")}>Delete</Button></td>
         </tr>

       )
     }
     )}
     </>)}
   </tbody>
 
   </Table>
  
   <Button variant="success" onClick={handleShow}>Add User</Button>{' '}

  </>
)};

export default NewFormik;
