import React  from "react";
import { useState, useEffect } from "react";
import {useNavigate, useParams} from "react-router-dom";
import {Field,Form,Formik,Formikprops} from 'formik';
import  Button  from "react-bootstrap/Button";
import {addFunc,getFunc} from "./Backenddata";
import './navbar.css';

function Home({setShow}){
const [user, setUser] =useState(null);
  let navigate = useNavigate();
  const newpath = ()=>{
    let path='./Users';
    navigate(path);
  }

  
  

    return(
         <div class="form">
          <>
        
        {user && (
            <Formik
        

        initialValues={{email:'',password:''}}
        validate={values => {
          const errors = {};
          console.log()
          if(!values.name){
              errors.name='fill the blanks';
          }else if(values.name<4){
              errors.name='name must be of 3 characters';
          }
          if(!values.email){
              errors.all='fill the blanks';
          }
          else if(values.email=='/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/'){
                 errors.all='enter valid format';
              }
              
          if(!values.password){
              errors.password='fill the blanks';
          }
          return errors;
        
        }}
        
        
        
onSubmit={async(values,{setErrors,setSubmitting}) => {
         
         if(values.email && values.password){
            try{
                const response = await addFunc(values.email,values.password)
            }catch(error){
                if(error.status===422){
                   setErrors(
                       {common : error.response.data.message}
                    )

                }
               
                setSubmitting(false);
            }
             }
           
            
          
         }
           
        
            
        }
        >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
        <div class="form-group">
          <form class="form-inline">
            
            <h2>Sign In Form</h2>
            <input
              type="email"
              name="email"
              placeholder='Enter your email..'
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            /><br></br>
           {errors.email && touched.email && errors.email}
           <input
              type="password"
              name="password"
              placeholder='Enter your password..'
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            /><br></br>
            {errors.password && touched.password && errors.password}
           
           
          <Button  class="btn btn-outline-info" variant="primary"  type="submit" value="Submit" onClick={handleSubmit}>
            Sign In
          </Button>

          {errors && errors.common && (<span class="error">{}</span>)}
          </form>
          </div>
          
        )}
        </Formik>
        )}
        
</>
</div>
)};
 

export default Home;
