import React, { useState , useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function ShowModal({Show,handleclose}) {
  const emailOnChange=(e)=>{
    setEmail(e.target.value);
}
     const passwordOnChange=(e)=>{
        setPassword(e.target.value);
         }

const formSubmit=(e) => {
    e.preventDefault();
    if(email =="" || password==""){
        SetIsError(true)
       
        alert("details should be filled")
    }else{
        for(let i of details){
            if(i.email === email && i.password === password ){
                
                alert("login successfully")
                return false;
                
        }
                alert("invalid")
                return false;
        
           
        }
    }
    }

 
 
 

  return (
  
    <>
      

      <Modal show={Show} onHide={handleclose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
          <input type="text"  value={name}  placeholder='Enter your name' onChange={nameOnChange}></input><br></br>

          <input type="Number"  value={age} placeholder='Enter your age' onChange={numberOnChange}></input><br></br>

          <input type="text"  value={role} placeholder='Enter your role' onChange={roleOnChange}></input>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleclose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleclose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ShowModal;
